import React, { useState } from 'react';
import AttendanceScanner from './AttendanceScanner';
import AttendanceRecord from './AttendanceRecord';

const HomePage = () => {
  const [showRecords, setShowRecords] = useState(false);

 return (
    <div className="min-h-screen p-8" style={{ backgroundColor: '#f5f7f8' }}>
      <div className="max-w-7xl mx-auto space-y-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Attendance Scanner Section */}
          <div className="lg:col-span-1">
            {/* The key forces a re-render and re-initialization of the scanner if records are hidden */}
            <div key={showRecords ? 'scanner-with-records' : 'scanner-only'} className="bg-white rounded-lg shadow">
              <div className="p-6">
                <h2 className="text-xl font-semibold mb-4" style={{ color: '#3e6268' }}>
                  Face Recognition Scanner
                </h2>
                <AttendanceScanner />
              </div>
            </div>
          </div>

          {/* Attendance Records Section */}
          <div className="lg:col-span-1">
            {showRecords ? (
              <AttendanceRecord />
            ) : (
              <div className="flex items-center justify-center h-full bg-white rounded-lg shadow p-6">
                <button
                  onClick={() => setShowRecords(true)}
                  className="px-6 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200"
                >
                  View Today's Attendance
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
