import { useState, useRef, useEffect } from "react"
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom"
import AttendanceScanner from "./pages/AttendanceScanner"
import AttendanceRecord from "./pages/AttendanceRecord"
import EmployeeRegistration from "./pages/EmployeeRegistration"
import Dashboard from "./pages/Dashboard"
import EmployeeList from "./pages/EmployeeList"
import HomePage from "./pages/HomePage"
import "./App.css"

function App() {
  const [isModelsLoaded, setIsModelsLoaded] = useState(false)
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)
  
  // Add ref for the navbar
  const navRef = useRef(null)

  // Handle clicks outside of navbar
  const handleClickOutside = (event) => {
    if (navRef.current && !navRef.current.contains(event.target)) {
      setIsMobileMenuOpen(false)
    }
  }

  // Add and remove event listener
  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside)
    return () => {
      document.removeEventListener('mousedown', handleClickOutside)
    }
  }, [])

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen)
  }

  // Close mobile menu when route changes
  useEffect(() => {
    setIsMobileMenuOpen(false)
  }, [window.location.pathname])

  return (
    <Router>
      <div className="App min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        {/* Navigation */}
        <nav ref={navRef} className="bg-white shadow-lg relative">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              <div className="flex items-center gap-8">
                <h1 className="text-2xl font-bold text-indigo-600">Smart Attendance</h1>
                <div className="hidden md:flex gap-4">
                  <Link to="/" className="px-4 py-2 text-gray-700 hover:text-indigo-600 transition">
                    Scanner
                  </Link>
                  <Link to="/register" className="px-4 py-2 text-gray-700 hover:text-indigo-600 transition">
                    Register
                  </Link>
                  <Link to="/employees" className="px-4 py-2 text-gray-700 hover:text-indigo-600 transition">
                    Employees
                  </Link>
                  <Link to="/dashboard" className="px-4 py-2 text-gray-700 hover:text-indigo-600 transition">
                    Dashboard
                  </Link>
                </div>
              </div>
              
              {/* Mobile menu button */}
              <div className="md:hidden">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleMobileMenu();
                  }}
                  className="p-2 rounded-md text-gray-700 hover:text-indigo-600 focus:outline-none"
                >
                  <svg
                    className="h-6 w-6"
                    fill="none"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    {isMobileMenuOpen ? (
                      <path d="M6 18L18 6M6 6l12 12" />
                    ) : (
                      <path d="M4 6h16M4 12h16M4 18h16" />
                    )}
                  </svg>
                </button>
              </div>
            </div>
          </div>

          {/* Mobile menu with transition */}
          <div 
            className={`md:hidden absolute w-full bg-white shadow-lg z-50 transform transition-transform duration-200 ease-in-out ${
              isMobileMenuOpen ? 'translate-y-0 opacity-100' : '-translate-y-full opacity-0'
            }`}
          >
            <div className="px-2 pt-2 pb-3 space-y-1">
              <Link
                to="/"
                className="block px-3 py-2 text-base font-medium text-gray-700 hover:text-indigo-600 hover:bg-gray-50 rounded-md"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Scanner
              </Link>
              <Link
                to="/register"
                className="block px-3 py-2 text-base font-medium text-gray-700 hover:text-indigo-600 hover:bg-gray-50 rounded-md"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Register
              </Link>
              <Link
                to="/employees"
                className="block px-3 py-2 text-base font-medium text-gray-700 hover:text-indigo-600 hover:bg-gray-50 rounded-md"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Employees
              </Link>
              <Link
                to="/dashboard"
                className="block px-3 py-2 text-base font-medium text-gray-700 hover:text-indigo-600 hover:bg-gray-50 rounded-md"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Dashboard
              </Link>
            </div>
          </div>
        </nav>

        {/* Routes */}
        <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/register" element={<EmployeeRegistration />} />
            <Route path="/employees" element={<EmployeeList />} />
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </main>
      </div>
    </Router>
  )
}

export default App
