import React, { useState, useEffect } from 'react';

// Mock AttendanceScanner component
const AttendanceScanner = () => {
  return (
    <div className="flex flex-col items-center justify-center p-6 bg-gray-50 rounded-lg border-2 border-dashed border-gray-300">
      <div className="w-64 h-64 bg-gray-200 rounded-lg flex items-center justify-center mb-4">
        <svg className="w-24 h-24 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
        </svg>
      </div>
      <p className="text-gray-600 text-center">Face Recognition Scanner</p>
    </div>
  );
};

// AttendanceRecord component with your code
const AttendanceRecord = () => {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading data
    setTimeout(() => {
      setAttendanceRecords([
        {
          id: 1,
          employeeName: 'John Doe',
          checkIn: new Date().setHours(8, 30, 0),
          checkOut: new Date().setHours(17, 15, 0),
          status: 'completed',
          timeStatus: 'Good'
        },
        {
          id: 2,
          employeeName: 'Jane Smith',
          checkIn: new Date().setHours(9, 15, 0),
          checkOut: null,
          status: 'active',
          timeStatus: 'Late'
        },
        {
          id: 3,
          employeeName: 'Mike Johnson',
          checkIn: new Date().setHours(8, 45, 0),
          checkOut: new Date().setHours(16, 30, 0),
          status: 'completed',
          timeStatus: 'Good'
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4" style={{ color: '#3e6268' }}>
        Today's Attendance Records
      </h2>
      {loading ? (
        <div className="text-center py-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 mx-auto" style={{ borderColor: '#3e6268' }}></div>
        </div>
      ) : attendanceRecords && attendanceRecords.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead style={{ backgroundColor: '#3e6268' }}>
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">
                  Employee Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">
                  Check In
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">
                  Check Out
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">
                  Time Status
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {attendanceRecords.map((record) => (
                <tr key={record.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{record.employeeName || 'N/A'}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-500">
                      {record.checkIn ? new Date(record.checkIn).toLocaleTimeString() : "N/A"}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-500">
                      {record.checkOut ? new Date(record.checkOut).toLocaleTimeString() : "Pending"}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span
                      className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        record.status === "completed" ? "bg-blue-100 text-blue-800" : "text-white"
                      }`}
                      style={record.status !== "completed" ? { backgroundColor: '#3e6268' } : {}}
                    >
                      {record.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {record.timeStatus && (
                      <span
                        className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                          record.timeStatus === "Late"
                            ? "bg-red-100 text-red-800"
                            : record.timeStatus === "Good"
                            ? "bg-green-100 text-green-800"
                            : "bg-yellow-100 text-yellow-800"
                        }`}
                      >{record.timeStatus}</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="text-center py-4 text-gray-500">No attendance records for today</div>
      )}
    </div>
  );
};

// HomePage component
const HomePage = () => {
  return (
    <div className="min-h-screen p-8" style={{ backgroundColor: '#f5f7f8' }}>
      <div className="max-w-7xl mx-auto space-y-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Attendance Scanner Section */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow">
              <div className="p-6">
                <h2 className="text-xl font-semibold mb-4" style={{ color: '#3e6268' }}>
                  Face Recognition Scanner
                </h2>
                <AttendanceScanner />
              </div>
            </div>
          </div>

          {/* Attendance Records Section */}
          <div className="lg:col-span-1">
            <AttendanceRecord />
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
