import React, { useState, useRef, useEffect } from 'react';
import { Camera } from 'lucide-react';

// Mock AttendanceScanner component
const AttendanceScanner = () => {
  const videoRef = useRef(null);
  const [isScanning, setIsScanning] = useState(false);
  const [scanResult, setScanResult] = useState(null);

  const startScanning = () => {
    setIsScanning(true);
    setScanResult(null);
    // Simulate scanning
    setTimeout(() => {
      setScanResult({
        success: true,
        name: "John Doe",
        time: new Date().toLocaleTimeString()
      });
      setIsScanning(false);
    }, 2000);
  };

  return (
    <div className="flex flex-col items-center justify-center">
      <div className="w-full max-w-md bg-gray-100 rounded-lg border-2 border-dashed border-gray-300 p-8 text-center">
        <div className="w-48 h-48 bg-gray-200 rounded-lg flex items-center justify-center mx-auto mb-4">
          <Camera className="w-20 h-20 text-gray-400" />
        </div>
        
        {scanResult ? (
          <div className="mt-4 p-4 bg-green-50 rounded-lg border border-green-200">
            <p className="text-green-800 font-semibold">{scanResult.name}</p>
            <p className="text-green-600 text-sm">Checked in at {scanResult.time}</p>
          </div>
        ) : (
          <p className="text-gray-600 mb-4">Position your face in front of the camera</p>
        )}

        <button
          onClick={startScanning}
          disabled={isScanning}
          className="mt-4 px-6 py-3 text-white rounded-lg font-semibold transition-all disabled:opacity-50"
          style={{ backgroundColor: '#3e6268' }}
        >
          {isScanning ? (
            <span className="flex items-center gap-2 justify-center">
              <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent"></div>
              Scanning...
            </span>
          ) : (
            'Start Scan'
          )}
        </button>
      </div>
    </div>
  );
};

// HomePage Component
const HomePage = () => {
  return (
    <div className="min-h-screen" style={{ backgroundColor: '#f5f7f8' }}>
      {/* Header with Logo */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-8 py-4">
          <div className="flex items-center gap-4">
            {/* Logo in top left */}
            <div className="w-12 h-12 rounded-full flex items-center justify-center text-white font-bold text-lg" style={{ backgroundColor: '#3e6268' }}>
              AS
            </div>
            <h1 className="text-2xl font-bold" style={{ color: '#3e6268' }}>
              Attendance System
            </h1>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto p-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Attendance Scanner Section */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-lg">
              <div className="p-6">
                <h2 className="text-xl font-semibold mb-4" style={{ color: '#3e6268' }}>
                  Face Recognition Scanner
                </h2>
                <AttendanceScanner />
              </div>
            </div>
          </div>

          {/* View Records Button Section */}
          <div className="lg:col-span-1 flex items-center justify-center">
            <button
              onClick={() => window.location.href = '/records'}
              className="group relative px-12 py-6 text-white text-lg font-semibold rounded-lg shadow-lg transition-all duration-300 transform hover:scale-105 hover:shadow-xl"
              style={{ backgroundColor: '#3e6268' }}
            >
              <span className="flex items-center gap-3">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
                View Attendance Records
                <svg className="w-5 h-5 transform group-hover:translate-x-1 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </span>
            </button>
          </div>
        </div>

        {/* Quick Stats Section */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 mb-1">Today's Check-ins</p>
                <p className="text-2xl font-bold" style={{ color: '#3e6268' }}>24</p>
              </div>
              <div className="w-12 h-12 rounded-full flex items-center justify-center" style={{ backgroundColor: '#3e626820' }}>
                <svg className="w-6 h-6" style={{ color: '#3e6268' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 mb-1">On Time</p>
                <p className="text-2xl font-bold text-green-600">20</p>
              </div>
              <div className="w-12 h-12 rounded-full flex items-center justify-center bg-green-100">
                <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 mb-1">Late Arrivals</p>
                <p className="text-2xl font-bold text-red-600">4</p>
              </div>
              <div className="w-12 h-12 rounded-full flex items-center justify-center bg-red-100">
                <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                </svg>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
