import React from 'react';
import AttendanceScanner from './AttendanceScanner';

const HomePage = () => {
 return (
    <div className="min-h-screen p-8" style={{ backgroundColor: '#f5f7f8' }}>
      <div className="max-w-7xl mx-auto space-y-8">
        {/* The key forces a re-render and re-initialization of the scanner */}
        <div key="scanner-only" className="bg-white rounded-lg shadow">
          <div className="p-6">
            <h2 className="text-xl font-semibold mb-4" style={{ color: '#3e6268' }}>
              Face Recognition Scanner
            </h2>
            <AttendanceScanner />
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
